#!/usr/bin/env bash
exec docker run --rm -v "$PWD:/build" --user root -e SIGNING_KEY --entrypoint bash ghcr.io/dadevel/archpkgs-builder:latest -c 'set -euo pipefail;echo "$SIGNING_KEY" | gpg --import -;mkdir ./public;mv ./artifacts/package-*/*.pkg.tar.zst ./public;for pkg in ./public/*.pkg.tar.zst;do gpg --detach-sign --output "${pkg}.sig" --sign "${pkg}";done;repo-add ./public/archpkgs.db.tar.zst ./public/*.pkg.tar.zst;echo "Options +Indexes" > ./public/.htaccess'
